#!/usr/bin/env python3

import json
import random
import requests
import json
import sys
import time
from urllib.parse import parse_qs, urlparse


HEADERS = {
    "User-Agent": "MiFit/4.6.0 (iPhone; iOS 14.0.1; Scale/2.00)"
}


def get_access_token(user: str, password: str) -> str:
    url = f"https://api-user.huami.com/registrations/+86{user}/tokens"
    data = {
        "client_id": "HuaMi",
        "password": password,
        "redirect_uri": "https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html",
        "token": "access"
    }
    resp = requests.post(url, data=data, headers=HEADERS, allow_redirects=False)
    query_str = urlparse(resp.headers["Location"]).query
    return parse_qs(query_str)["access"]


def get_login_token_info(access_token: str) -> dict:
    url = "https://account.huami.com/v2/client/login"
    data = {
        "app_name": "com.xiaomi.hm.health",
        "app_version": "4.6.0",
        "code": access_token,
        "country_code": "CN",
        "device_id": "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1",
        "device_model": "phone",
        "grant_type": "access_token",
        "third_name": "huami_phone",
    }
    resp = requests.post(url, data=data, headers=HEADERS).json()
    print(resp.get("thirdparty_info"))
    return resp["token_info"]


def get_app_token(login_token_info: dict) -> str:
    url = "https://account-cn.huami.com/v1/client/app_tokens"
    params = {
        "app_name": "com.xiaomi.hm.health",
        "dn": "api-user.huami.com,api-mifit.huami.com,app-analytics.huami.com",
        "login_token": login_token_info["login_token"],
    }
    resp = requests.get(url, params=params, headers=HEADERS).json()
    return resp["token_info"]["app_token"]


def update_band_data(login_token_info: dict, app_token: str, data: dict) -> dict:
    url = "https://api-mifit-cn.huami.com/v1/data/band_data.json"
    params = dict(t=int(time.time() * 1000))
    headers = HEADERS.copy()
    headers["apptoken"] = app_token
    data = {
        "userid": login_token_info["user_id"],
        "last_sync_data_time": 1597306380,
        "device_type": 0,
        "last_deviceid": "DA932FFFFE8816E7",
        "data_json": json.dumps(data),
    }
    return requests.post(url, params=params, data=data, headers=headers).json()


def mock_step_data(step: int) -> dict:
    stage = {
        "start": 327,
        "stop": 341,
        "mode": 1,
        "dis": int(step * 0.8),
        "cal": int(step * 0.8) // 37,
        "step": step,
    }

    now = int(time.time())
    slp = {k: 0 for k in "dp lt wk wc is lb to dt rhr ss".split()}
    slp.update(st=now, ed=now, usrSt=-1440, usrEd=-1440)

    summary = {
        "v": 6,
        "slp": slp,
        "stp": {
            "ttl": step,
            "dis": stage["dis"],
            "cal": stage["cal"],
            "wk": 41,
            "rn": 50,
            "runDist": 0,
            "runCal": 0,
            "stage": [stage],
        },
        "goal": 8000,
        "tz": "28800"
    }

    return [
        {
            "data_hr": "",
            "date": time.strftime("%F"),
            "data": [
                {
                    "start": 0,
                    "stop": 1439,
                    "value": """
                        
                    """.strip(),
                    "tz": 32,
                    "did": "DA932FFFFE8816E7",
                    "src": 24
                }
            ],
            "summary": json.dumps(summary),
            "source": 24,
            "type": 0
        }
    ]


def main(user: str, password: str, step_min=18000, step_max=20000):
    access_token = get_access_token(user, password)
    login_token_info = get_login_token_info(access_token)
    app_token = get_app_token(login_token_info)

    step = random.randint(int(step_min), int(step_max))
    print("Random steps:", step)
    band_data = mock_step_data(step)
    update_band_data(login_token_info, app_token, band_data)


if __name__ == "__main__":
    main(*sys.argv[1:])
